TestCase.Resource:

SELECT * FROM (
 SELECT a, b, c, RANK() OVER(PARTITION BY a ORDER BY b) rk FROM (
   SELECT a, b, c FROM x ORDER BY a, b
 )
) WHERE rk <= 10


SELECT * FROM (
 SELECT a, c, RANK() OVER(PARTITION BY a ORDER BY a) rk FROM (
   SELECT a, COUNT(c) AS c FROM x GROUP BY a
 )
) WHERE rk <= 10
